
let interval, t=0;
function simulate(){
  postMessage({dlStatus: (Math.random()*100).toFixed(2), ulStatus: (Math.random()*30).toFixed(2), pingStatus: (Math.random()*50).toFixed(1)});
  t++; if(t<30) setTimeout(simulate, 300); else postMessage("done");
}
onmessage = function(e){ if(e.data=="start"){ t=0; simulate(); } }
